INSERT INTO `album` (`album_id`, `size`, `name`, `type`, `artist`, `artist_id`, `profile`, `publish_time`, `pic_url`) VALUES
	(1, 10, '渡 The Crossing', '摇滚', '薛之谦', 1, '2017年华语音乐圈最受期待唱片作品 全能唱作奇才薛之谦掏心力作 昨日渡往明日 从回忆渡往梦想 从现实渡往幻境 薛之谦【渡 The Crossing】正式发行', '2019-01-02', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/1.jpg'),
	(2, 28, 'TVアニメ「WHITE ALBUM2」ORIGINAL SOUNDTRACK', '摇滚', '小林俊太郎', 2, '2013年10月より放映のTVアニメ『WHITE ALBUM2』のオリジナル・サウンドトラック。原作のPS3/PSV『WHITE ALBUM2 幸せの向こう側』で使用されたBGMを、小林俊太郎がリアレンジ。音楽監督は、原作のプロデューサーでもある下川直哉が担当。コンサートホールやスタジオで録音したハイクオリティな音源を収録。2017年华语音乐圈最受期待唱片作品\n\n全能唱作奇才薛之谦掏心力作\n\n昨日渡往明日\n\n从回忆渡往梦想\n\n从现实渡往幻境\n\n薛之谦【渡 The Crossing】正式发行', '2008-07-18', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/2.jpg'),
	(3, 23, 'ゼルダの伝説 ブレス オブ ザ ワイルド SOUND SELECTION (塞尔达传说 荒野之息 游戏原声)', '电音', '片岡真央', 3, 'ゼルダの伝説 ブレス オブ ザ ワイルド SOUND SELECTION (塞尔达传说 旷野之息 NS游戏音乐选集)', '2006-11-08', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/3.jpg'),
	(4, 10, '意外', '摇滚', '薛之谦', 1, '筹备新专辑的这段时间里，薛之谦有了一个新的身份“薛老板”，他投资的火锅店“上上谦”在短短时间内开出了三家分店，创立的女装品牌“uujuly”也在短时间内荣登淘宝“星店”的销售冠军，有人问薛之谦：“真没看出来，你还是个商业奇才啊。”其实只有他自己知道，成功背后付出的努力是多少。\r\n\r\n于是“薛老板”把这种精神带回了他的音乐事业，在这张全新的创作专辑中担起了专辑制作人的重担.\r\n\r\n', '2011-10-15', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/4.jpg'),
	(5, 2, '怪咖', '流行', '薛之谦', 1, '华灯初上，舞台炫目之下戴着面具的小丑转身谢幕，日光下的嬉笑，阴影里的伤感，只有他自己于暗夜独自舔舐伤口。在盛大而华丽的故事里，隐约上演着“哗众取宠”的角色设定，大家有着不同的假面，扮演着不同的角色，演绎着不同的经历，却有着相同的悲哀。退步以后是取悦，取悦也需要个称谓，失去喜怒的怪咖们，有序离场吧', '2015-12-08', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/27.jpg'),
	(6, 43, 'La La Land - The Complete Musical Experience', '轻音乐', 'D.A.', 4, '《爱乐之城》是一部于2016年上映的美国爱情歌舞片，由达米恩·查泽雷担任导演和编剧。电影主要演员有雷恩·高斯林、艾玛·斯通、J·K·西蒙斯、芬恩·维特罗克、罗丝玛丽·德威特和梅根·费、约翰·传奇。本片为高斯林与斯通继《熟男型不型》、《匪帮传奇》后第三部合作饰演一对恋人的电影。', '2010-02-22', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/6.jpg'),
	(7, 16, 'Speak Now World Tour Live', '摇滚', 'Taylor Swift', 13, '美国乡村小天后泰勒·斯威夫特（Taylor Swift）日前宣布将发行全新演唱会专辑《Speak Now World Tour - Live》，而这张专辑将记录了泰勒2011年‘现在说出来’世界巡回演唱会(Taylor Swift Speak Now World Tour 2011)并计划在美国时间11月21日发行。', '2011-08-22', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/25.jpg'),
	(8, 3, '宫崎峻电影主题曲全集 - 与Ghibli同行', '轻音乐', '久石让 (ひさいし じょう)', 14, '宫崎骏是第一位将动画上升到人文高度的思想者，同时也是日本三代动画家中承前启后的精神支柱。\r\n\r\n这张唱片是GHIBLI（宫崎骏工作室）近年来的最著名的十二部动画的主题曲和片尾曲的合集。\r\n\r\n这张专辑与Joe Hisaishi没有百分百的关系，只是大家最耳熟能详的Joe Hisaishi的作品几乎全是出自宫崎骏的动画，所以就将其归类到Joe Hisaishi系列。', '2013-10-09', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/8.jpg'),
	(9, 17, '古惑仔 最强精选集 电影原声带', '流行', '谢天华', 15, '从「人在江湖」、「猛龙过江」、「只手遮天」、「战无不胜」到「龙争虎斗」，一连五部的「正牌」古惑仔电影每部都是票房冠军，同时也捧红了郑伊健陈小春等人，而且每一张原声大碟都是一时之选。这张《古惑仔最强精选》收录了古惑仔系列的主题曲及插曲，包括「友情岁月」、「战无不胜」、「刀光剑影」、「甘心替代你」等红极一时的歌曲，由郑伊健、陈小春、林晓峰等主要演员演唱，古惑仔迷绝对不能错过！', '2011-02-23', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/9.jpg'),
	(10, 19, '张国荣钢琴精选集', '轻音乐', '张国荣', 18, '当你看见天上星星....可有想起.....\r\n\r\n哥哥18首经曲\r\n\r\n钢琴+提琴+重新演绎 DSD数码混制', '2011-06-13', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/10.jpg'),
	(11, 1, 'EVERGREEN SONGS 2014', '轻音乐', '初音ミク', 19, '2014年に発表された「初音ミク」楽曲の中から動画サイトで多く再生され100万再生超で”伝説入り”を果たした楽曲をはじめ、これだけは押さえておきたい楽曲をまとめたレンタル専用CD。\r\n\r\nジャケットイラストは奈津ナツナ氏が手掛ける。', '2011-06-20', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/11.png'),
	(12, 1, 'The Best of G.E.M. 2008-2012', '流行', 'G.E.M. 邓紫棋', 20, '《The Best of G.E.M. 2008-2012》\r\n\r\n收录24首邓紫棋经典歌曲，包括两首从未发行演唱会版本歌曲Lover和A.I.N.Y.。\r\n\r\nSecond Version加赠《你把我灌醉》、《奇迹 (Acoustic Mix)》。', '2010-12-04', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/12.jpg'),
	(13, 1, '18...', '流行', 'G.E.M. 邓紫棋', 20, '邓紫棋（G.E.M.）出道仅两年，凭突出的唱功和创作的才华，让她首张EP《G.E.M.》一推出便深受欢迎，2009年度在四台联颁音乐大奖获「卓越大奖」银奖，并特别在她的18岁生辰推出第二张大碟《18...》，再次展示唱功和创作力。早前到美国洛杉矶为新唱片录音，至于随限量版专辑附上的写真集和主打歌的MV，更专程到日本取景。', '2017-06-05', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/13.jpg'),
	(14, 1, 'Sad Sometimes (feat. 黄霄云)', '流行', 'Alan Walker', 21, 'LIQUID STATE LIMITED', '2015-04-06', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/14.jpg'),
	(15, 1, 'All Falls Down', '流行', 'Alan Walker', 21, '《All Falls Down》由挪威DJ、音乐制作人Alan Walker、英国DJ Digital Farm Animals联合制作，瑞典歌手Juliander、美国新星歌手麦莉·赛勒斯（Miley Cyrus）的亲妹妹诺雅·塞勒斯（Noah Cyrus）演唱，于2017年10', '2022-09-29', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/15.jpg'),
	(16, 2, 'GREATEST HITS 2011-2017 "ALTER EGO"', '摇滚', 'EGOIST', 22, '12月22日にて配信スタート！\r\n\r\nEGOIST、5年ぶりのアルバムは初となるBEST ALBUM!', '2008-01-08', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/16.jpg'),
	(17, 1, 'Timbaland Presents: Shock Value', '轻音乐', 'Timbaland', 23, 'Timbaland Presents Shock Value是一张仅仅有着几支优秀作品的专辑。在其试图和rock结合的部分，Timbaland无非也只是进行了一些声音的处理，在rock中加入一些Hip-Hop的节奏，将部分声音urban化。Timbaland没有找到切入点和结合点，没有足够的能力实现其远大的目标。。', '2019-12-05', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/17.jpg'),
	(18, 2, 'Believe', '轻音乐', 'Justin Bieber', 24, '录音室专辑', '2015-05-12', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/18.png'),
	(19, 1, '展翅高飞Let Go', '电音', 'Avril Lavigne', 25, '艾薇儿的第一张专辑，获得多项大奖 。她的唱腔炫耀似的活力四射，她的嗓音一如水晶般清澈，而她的歌词更是流露出纯粹的女性风格。“Anything But Ordinary”是一首赞扬个体独特性的颂歌；', '2008-09-25', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/19.png'),
	(20, 1, 'Girlfriend', '流行', 'Avril Lavigne', 25, '加拿大歌手Avril Lavigne开创了史无前例的一步将她的国际fans联系起来－－将她的新歌Girlfriend（女朋友）用八国语言进行录制！“Avril为此付出了极大的努力，”她的经纪人Terry McBride说到，“她想把它们做好和做准确，她不想让其他人代唱她的歌。”', '2011-04-27', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/20.jpg'),
	(21, 1, 'Head Above Water', '流行', 'Avril Lavigne', 25, '在艾薇儿的第六张完整专辑和BMG首演的过程中，她毫无保留。她以胜利者的姿态详述了每一次的磨难，并且比以往任何时候都更加强大。你将会听到这位女歌手的声音。"这是我和我的斗争，"她肯定地说，"这张专辑讲述了我自己的故事。"', '2021-07-13', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/21.jpg'),
	(22, 1, 'My Happy Ending', '流行', 'Avril Lavigne', 25, 'My Happy Ending 的旋律和伴奏都给听觉和意识上的冲击很大，加上艾薇儿清澈唯美的嗓音，简直是我这辈子听过最好听的音乐了！', '2011-11-01', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/22.jpg'),
	(23, 1, 'The Best Damn Thing', '电音', 'Avril Lavigne', 25, '当艾薇儿在2004年为了她的上一张在全球创下八百万张销售佳绩的专辑“酷到骨子里（Under My Skin）”巡回表演的时候，这位在加拿大出生的庞克-流行劲妹发现她比较偏好表演速度与节奏较快的歌曲，于是，她决定要录制一张专辑，这张专辑要能捕捉到这种让她在舞台上尽情宣泄的冲劲十足、火力全开的超高能量。', '2019-06-18', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/23.jpg'),
	(24, 1, 'Wish You Were Here', '摇滚', 'Avril Lavigne', 25, '摇滚创作才女艾薇儿睽违两年的新作《Goodbye Lullaby》正式确定将于明年2011年3月8日全球发行。首支单曲〈What the Hell〉将于美国纽约的跨年时段全球首播。', '2015-09-18', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/24.jpg'),
	(25, 1, 'Bad Blood', '轻音乐', 'Taylor Swift', 13, '七座格莱美奖得主 史上最年轻格莱美最佳专辑殊荣肯定', '2023-01-19', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/25.jpg'),
	(26, 1, '黄家驹原作精选集', '摇滚', 'BEYOND', 26, '当第一次听到BEYOND的声音，心灵里回荡的是一种源远流长的骨气和正义。因为他们都是中国人，我想这是我喜欢他们的音乐的原因。虽然广东的语言使他们的歌曲似乎有点不能广泛流传，但是一种共同的感觉是铿锵和渴望。家驹是BEYOND的灵魂，', '2019-07-02', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/26.jpg'),
	(27, 1, 'BINGBIAN病变（女声版）', '摇滚', '鞠文娴', 27, '鞠文娴带来女生版全新单曲《BINGBIAN病变》；清晨的太阳没有什么不同，像往常一样起床，翻看着昨天我们一起看的留照片；此刻的我，没有任何情绪，脑海唯独留着跟你的回忆。', '2016-02-19', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/27.jpg'),
	(28, 1, 'As If It\'s Your Last (마지막처럼)', '电音', 'BLACKPINK', 28, 'Cover | Chorus | Mix：静静全曲自和声，rap假自帅，就酱。【swag】', '2009-03-07', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/28.jpg'),
	(29, 1, 'SQUARE UP', '电音', 'BLACKPINK', 28, 'YG旗下最具代表性的女子组合BLACKPINK凭借[SQUARE ONE]、[SQUARE TWO]，以及歌曲《AS IF IT’S YOUR LAST》证明了他们多样的魅力，并迷倒了大众，此次BLACKPINK携众所期盼的首张迷你专辑[SQUARE UP]华丽回归。', '2014-05-11', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/29.jpg'),
	(30, 1, 'Love Drunk', '电音', 'Boys Like Girls', 29, 'Boys Like Girls的第二张新专辑，这张专辑一半在纽约完成，另一半在温哥华完成。不同的制作团队，不同的环境，想来听着奇妙里面有这两种不同的灵感激发...这张专辑在发第一首单曲"Love Drunk"时，就得到不少人的关注，', '2016-03-23', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/30.jpg'),
	(31, 1, 'Life Is Beautiful', '摇滚', 'Bronn Journey', 30, 'American singer-songwriter Lana Del Rey is ready to bring back another nostalgic anthem to our lives with upcoming new single “Life Is Beautiful“.\r\n\r\n', '2011-01-31', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/31.jpg'),
	(32, 1, '古巨基精选', '摇滚', '古巨基', 31, '2005年，古巨基推出的《劲歌金曲》新曲加精选横扫当年销量大奖。继当年一曲长达10分钟、集结了39首经典名曲的medley“劲歌金曲”后，2008年基仔再度出击，以“劲歌金曲2 - 情歌王”再续情缘，', '2011-04-10', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/32.jpg'),
	(33, 1, 'Call Me Maybe', '轻音乐', 'Carly Rae Jepsen', 32, 'Justin Bieber力挺的新人——Carly Rae Jepsen是一位来自加拿大的女歌手。2007年夏天，22岁的Carly Rae Jepsen参加了加拿大当地的“Canadian Idol”选秀节目。表现突出的她获得了第3名，虽说最后没有如愿夺下后冠，', '2015-08-10', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/33.jpg'),
	(34, 1, 'Wrap Me In Plastic', '流行', 'CHROMANCE', 33, 'Brand new remix of very successful original song of Wrap Me In Plastic.', '2008-10-20', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/34.jpg'),
	(35, 1, 'Complicated', '摇滚', 'Avril Lavigne', 25, '艾薇儿的第一张专辑，获得多项大奖 。她的唱腔炫耀似的活力四射，她的嗓音一如水晶般清澈，而她的歌词更是流露出纯粹的女性风格。“Anything But Ordinary”是一首赞扬个体独特性的颂歌；', '2008-09-09', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/35.jpg'),
	(36, 2, ' good', '轻音乐', 'CRITTY', 34, '是首正中爱情骗子要害的简洁歌曲；沾染了弦乐色彩的“I’m With You”反映了Avril较为温和的一面；“Losing Grip”和“Unwanted”这些歌曲，勇敢地面对了拒绝与背叛这些主题所需要的沈重与悲哀；而接下来的“My World”与具有暗喻意味的“Mobile”，则是完美的展现了Avril的生活经验。', '2022-03-12', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/36.jpg'),
	(37, 1, 'WONDER Tourism', '轻音乐', 'DAISHI DANCE/Cecile Corbel', 35, 'DAISHI DANCEの3年ぶり(2012年時)通算4枚目となるオリジナル・アルバム。スタジオジブリ映画『借りぐらしのアリエッティ』主題歌の作詞作曲を担当したブルターニュのシンガーでハープ奏者のCecile Corbelに加え、Crystal kayなど多彩なアーティストが参加。', '2012-07-08', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/22.jpg'),
	(38, 1, 'Unforgettable', '电音', 'Dana Winner', 36, '比利时女歌手Dana Winner 音色纯净，音域宽广的她在1988年一次混音竞赛中以一首由Mireille Mathieu唱的Amour D�fendu的获胜次年，发行了第一张单曲翻唱卡朋特的Top of the World从此走上演艺事业。', '2018-05-30', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/38.jpg'),
	(39, 1, 'Run Free', '流行', 'Deep Chills', 38, '从此走上演艺事业。', '2022-10-17', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/39.jpg'),
	(40, 1, 'Safe Trip Home', '流行', 'Dido', 39, '沉淀了五年，DIDO再次要用她的声音抚慰都市中每一颗不设防的心灵，Safe Trip Home就像是Life For Rent的一个延续，向人们昭示这位女神的回归.', '2007-10-13', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/40.jpg'),
	(41, 1, 'Love Me Like You Do (From "Fifty Shades of Grey")', '摇滚', 'Ellie Goulding', 40, '故事讲述了一名纯真的21岁女大学生安娜斯塔西娅-斯蒂尔(Anastasia Steele，原型为“贝拉”)因为为校报作一篇报道，前去采访28岁英俊的企业家克里斯蒂安-格雷(Christian Grey，原型为“爱德华”)，两人之间擦出了爱的火花，但很快安娜就发现格雷的一个惊人的秘密：', '2021-08-15', 'https://web-music-player.oss-cn-guangzhou.aliyuncs.com/cover/41.jpg'),
